import { useState } from "react";
import axios from "axios";

import { ChatOutput } from "./ChatOutput";
export const ChatBot = (props) => {
    const [input, setInput] = useState("");
    const [chatOutput, setChatOutput] = useState([]);

    async function SubmitHandler(event) {
        event.preventDefault();
        setChatOutput([
            ...chatOutput,
            { userInput: input, serverResponse: "loading" },
        ]);
        setInput("");
        const response = await axios.get("https://catfact.ninja/fact?max_length=140");
        setChatOutput((prevChatOutput) => {
            const updatedChatOutput = [...prevChatOutput];
            const loadingIndex = updatedChatOutput.findIndex(
                (message) => message.serverResponse === "loading"
            );
            if (loadingIndex !== -1) {
                updatedChatOutput[loadingIndex].serverResponse = response.data.fact;
            }
            return updatedChatOutput;
        });

    }
    return (
        <div className="chatbot">
            {/* <aside className="sidemenu">
                <div className="side-menu-button">
                    <span>+</span>
                    New chat
                </div>
            </aside> */}
            <section className="chatbox">
                <div className="output">

                    {chatOutput.map((message, index) => (
                        <ChatOutput key={index} message={message} />
                    ))}
                </div>

                <div className="chat-input-holder">
                    <textarea
                        value={input}
                        rows="1"
                        className="chat-input-textarea"
                        onChange={(e) => setInput(e.target.value)}
                    >
                    </textarea>
                    <button onClick={SubmitHandler} type="submit" className="chatbot-submit"><svg xmlns="http://www.w3.org/2000/svg" height="24" viewBox="0 0 24 24" width="24"><path d="M0 0h24v24H0z" fill="none" /><path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z" /></svg></button>
                </div>
            </section>
        </div>
    )
}
