import { useState } from "react";
import { ChatMessage } from "./ChatMessage";
import axios from "axios";
import {ResponseMessage} from "./ResponseMessage";
export const ChatBot = (props) => {
    const [input, setInput] = useState("");
    const [chatLog, setChatLog] = useState([]);
    const [serverResponse, setServerResponse] = useState([]);

    async function SubmitHandler(event) {
        event.preventDefault();
        setInput("");
        setChatLog([...chatLog, { user: "me", message: `${input}` }]);
        const response = await axios.get("https://catfact.ninja/fact?max_length=140");
        setServerResponse([...serverResponse, { user: "ai", message: `${response.data.fact}` }])
        console.log(serverResponse);
    }
    return (
        <div className="chatbot">
            <aside className="sidemenu">
                <div className="side-menu-button">
                    <span>+</span>
                    New chat
                </div>
            </aside>
            <section className="chatbox">
                <div className="chat-log">
                    {chatLog.map((message, index) => (
                        <ChatMessage key={index} message={message} />
                    ))}

                </div>

                <div className="server-log">
                    {serverResponse.length > 0 && serverResponse.map((message, index) => (
                        <ResponseMessage key={index} message={message} />
                    ))}
                </div>

                <div className="chat-input-holder">
                    <form onSubmit={SubmitHandler}>
                        <textarea
                            value={input}
                            rows="1"
                            className="chat-input-textarea"
                            onChange={(e) => setInput(e.target.value)}
                        >
                        </textarea>
                        <button type="submit" className="green focus dark">Submit here</button>
                    </form>
                </div>
            </section>
        </div>
    )
}
