import { useState } from "react";
import { myAxios } from "../services/helper";
export const SideBarForChat = (props) => {
    const List = [{ name: "akhil", description: "gg" }, { name: "akhil", description: "gg" }, { name: "akhil", description: "gg" }, { name: "akhil", description: "gg" }];
    const [chatCreated,setChatCreated]=useState(false);
    function createChat(index) {
        if(chatCreated){
            return ;
        }
        setChatCreated(true);
        const sendDatatoApi = {
            userInput: "",
            serverResponse: "",
            id: index

        }
        const responseFromapi = myAxios.post("/auth/new-chat", sendDatatoApi).then((response) => response.data).then((response) => {
            console.log(response);
        }).catch((tokenerror) => {
            console.log(tokenerror);
        })
        console.log("sidecharthere");   
        console.log(responseFromapi);
    }

    return (

        <div className="flex  bg-white">
            <div className="flex flex-col  border-r-2 overflow-y-auto">
                <div className="border-b-2 py-4 px-2">
                    <input
                        type="text"
                        placeholder="search chatting"
                        className="py-2 px-2 border-2 border-gray-200 rounded-2xl w-full"
                    />
                </div>

                {List.map((ListItem, index) => {
                    return (
                        <a href={`/chat/${index}`} key={index} onClick={createChat(index)}>
                            <div className="flex flex-row py-4 px-2 justify-center items-center border-b-2">

                                <div className="w-full px-2">
                                    <div className="text-lg font-semibold">{ListItem.name}</div>
                                    <span className="text-gray-500">{ListItem.description}</span>
                                </div>
                            </div>
                        </a>
                    )
                })}

            </div>
        </div>
    )
}
