import { useState } from "react";
import axios from "axios";

import { ChatOutput } from "./ChatOutput";
export const ChatBot = (props) => {
    const [input, setInput] = useState("");
    const [chatOutput, setChatOutput] = useState([]);

    async function SubmitHandler(event) {
        event.preventDefault();
        setInput("");
        const response = await axios.get("https://catfact.ninja/fact?max_length=140");
        setChatOutput([...chatOutput, { userInput: `${input}`, serverResponse: `${response.data.fact}` }])
    }
    return (
        <div className="chatbot">
            {/* <aside className="sidemenu">
                <div className="side-menu-button">
                    <span>+</span>
                    New chat
                </div>
            </aside> */}
            <section className="chatbox">
                <div className="output">
                    {chatOutput.map((message, index) => (
                        <ChatOutput key={index} message={message} />
                    ))}
                </div>

                <div className="chat-input-holder">
                    <form onSubmit={SubmitHandler}>
                        <textarea
                            value={input}
                            rows="1"
                            className="chat-input-textarea"
                            onChange={(e) => setInput(e.target.value)}
                        >
                        </textarea>
                        <button type="submit" className="chatbot-submit">Submit here</button>
                    </form>
                </div>
            </section>
        </div>
    )
}
