import { useEffect, useState } from "react";
import axios from "axios";
import { myAxios } from "../services/helper";
import { ChatOutput } from "../utilities/ChatOutput";
import { SideBarForChat } from "./SideBarForChat";
import { useParams } from "react-router-dom";
export const ChatScreen = () => {
    const [input, setInput] = useState("");
    const [chatOutput, setChatOutput] = useState([]);
    const [userInput, setUserInput] = useState("");
    const [serverResponse, setServerResponse] = useState("");
    const [Data, setData] = useState([]);
    let { index } = useParams();

    useEffect(() => {
        fetchInitailData();
    }, []);
    function fetchInitailData() {
        const allChatsResponse = myAxios.get(`/auth/chat/${parseInt(index) + 1}`).then((response) => response.data).then((response) => {
            setData([...Data, allChatsResponse]);
        }).catch((error) => {
            console.log(error);
        })
    }


    console.log(Data);


    async function SubmitHandler(event) {
        event.preventDefault();
        setUserInput(userInput + "- " + input);
        setChatOutput([
            ...chatOutput,
            { userInput: input, serverResponse: "loading" },
        ]);

        const response = await axios.get("https://catfact.ninja/fact?max_length=140");
        setChatOutput((prevChatOutput) => {
            const updatedChatOutput = [...prevChatOutput];
            const loadingIndex = updatedChatOutput.findIndex(
                (message) => message.serverResponse === "loading"
            );
            if (loadingIndex !== -1) {
                updatedChatOutput[loadingIndex].serverResponse = response.data.fact;
            }
            return updatedChatOutput;
        });

        setServerResponse(serverResponse + "-" + response.data.fact);
        const sendDatatoApi = {
            userInput: userInput,
            serverResponse: serverResponse,
            id: parseInt(index) + 1

        }

        const responseFromapi = myAxios.post("/auth/chat", sendDatatoApi).then((response) => response.data).then((response) => {
            console.log(response);
        }).catch((tokenerror) => {
            console.log(tokenerror);
        })
        console.log(responseFromapi);
        setInput("");

    }
    return (
        <div className="chat">
            <SideBarForChat />

        </div>
    )
}
